mapper.insert:
	INSERT INTO TB_IP_ACL 
	(
    	pk_ip_acl
    ,   fd_explain
    ,   fd_ip_1
    ,   fd_ip_2
    ,   fd_ip_3
    ,   fd_ip_4
    ,   fd_menu_yn
    ,   fd_admin_id
    ,   fd_reg_date			
	) 
	VALUES
	(
		TB_IP_ACL_SEQ.NEXTVAL
    ,   #{fd_explain}
    ,   #{fd_ip_1}
    ,   #{fd_ip_2}
    ,   #{fd_ip_3, jdbcType=VARCHAR}
    ,   #{fd_ip_4, jdbcType=VARCHAR}
    ,   #{fd_menu_yn}
    ,   #{fd_admin_id}
    ,   #{fd_reg_date}		
	)

mapper.update:
UPDATE TB_IP_ACL SET
  fd_explain	=	#{fd_explain}
, fd_ip_1		=	#{fd_ip_1}
, fd_ip_2		=	#{fd_ip_2}
, fd_ip_3		=	#{fd_ip_3, jdbcType=VARCHAR}
, fd_ip_4		=	#{fd_ip_4, jdbcType=VARCHAR}			
, fd_menu_yn		=	#{fd_menu_yn}
WHERE pk_ip_acl = #{pk_ip_acl}
